def add_korean_func(Eword_list, Eword_dic):

   #각 알파벳이 사전에서 시작되는 줄 번호 인덱스
   alphabet_line_num_list = [1,5965,11522,22975,29322,33732,39445,44051,48916,53809,54707,55709,59225,64904,67971,70413,79110,79639,87921,98521,103923,106180.107709,110308,110440,110804]

   alphabet_num = 0
   for i in range(len(Eword_list)):
      found_word = Eword_list[i]
      #추출한 단어

      with open('English-Korean_dic.txt', mode = 'r', encoding='utf-8') as file:

            alphabet_num = ord(found_word[0].lower())
            alphabet_num -= 97 #a부터 0으로 인덱스 시작

            for line_num, line in enumerate(file, start=1):
               #시작하고 싶은 줄 번호에 도달하면 작업 시작
               if line_num >= alphabet_line_num_list[alphabet_num]:
                  dic_word = line.split('/')[0].strip()
                  if found_word.lower() == dic_word.lower(): 
                     Eword_dic[f'{dic_word}'] = line.split('/')[1].strip()
                     break
                  else:
                     if found_word[0].lower() != dic_word[0].lower():
                        #알파벳 하나를 다 순회해도 해당되는 단어가 없음
                        Eword_dic[f'{found_word}'] = '단어검색실패'
                        break
                     else:
                        continue
                     

